# -----------------------------------------------------------------------------
# LOGGER
# -----------------------------------------------------------------------------

verboselogs.install()
logger = verboselogs.VerboseLogger("module_logger")


def setup_logging(level: str = "INFO") -> None:
    """Configure logging for this subpackage."""

    coloredlogs.install(level=level, logger=logger)
    logger.setLevel(level)


setup_logging()

