from loguru import logger

# Import dashboard components
sys.path.append(str(Path(__file__).parent))
from dashboard import create_dashboard_interface, create_dashboard_api, dashboard_state, DeploymentInfo

# üéØ Create Modal App
app = modal.App("{app_name}")

# üê≥ Container Image Configuration (Optimized with ML packages)
{image_config}

# üì¶ Original Application Code
{original_code}

# ‚ö° Modal Function Configuration with GPU
@app.function(
    image=image,
    gpu="any",  # Request GPU support
    min_containers=1,
    max_containers=1,
    timeout={timeout_seconds},
    scaledown_window={scaledown_window},
    memory=16384,  # 16GB RAM for ML workloads
)
@modal.concurrent(max_inputs=100)
@modal.asgi_app()
def deploy_gradio():
    """Deploy Gradio app with GPU support and dashboard on Modal."""

    # Check GPU availability
    import torch
    gpu_available = torch.cuda.is_available()
    gpu_name = torch.cuda.get_device_name(0) if gpu_available else "None"

    # Initialize deployment info
    deployment_info = DeploymentInfo(
        app_name="{app_name}",
        deployment_mode="optimized",
        deployment_time=datetime.now().isoformat(),
        modal_version=modal.__version__,
        python_version=f"{{sys.version_info.major}}.{{sys.version_info.minor}}.{{sys.version_info.micro}}",
        gpu_enabled=gpu_available,
        timeout_seconds={timeout_seconds},
        max_containers=1,
        environment={{
            **{{k: v for k, v in os.environ.items() if k.startswith("MODAL_")}},
            "GPU_AVAILABLE": str(gpu_available),
            "GPU_NAME": gpu_name,
            "TORCH_VERSION": torch.__version__,
        }}
    )
    dashboard_state.set_deployment_info(deployment_info)

    logger.info(f"Starting Modal deployment in optimized mode (GPU: {{gpu_available}})")
    if gpu_available:
        logger.info(f"GPU detected: {{gpu_name}}")

    # üîç Detect Gradio Interface
    demo = None
    interface_names = ['demo', 'app', 'interface', 'iface']

    for name in interface_names:
        if name in globals() and hasattr(globals()[name], 'launch'):
            demo = globals()[name]
            logger.info(f"Found Gradio interface: {{name}}")
            break

    if demo is None:
        for var_name, var_value in globals().items():
            if hasattr(var_value, 'queue') and hasattr(var_value, 'launch'):
                demo = var_value
                logger.info(f"Found Gradio interface through scanning: {{var_name}}")
                break

    if demo is None:
        logger.error("No Gradio interface found")
        raise ValueError("Could not find Gradio interface")

    # üé® Create Dashboard
    dashboard = create_dashboard_interface(demo)
    dashboard.queue(max_size=20)  # Higher queue for ML workloads

    # üîó FastAPI Setup
    fastapi_app = FastAPI(
        title="{app_name} - Modal Dashboard (Optimized)",
        description="Optimized deployment with GPU support and monitoring dashboard",
        version="1.0.0",
        docs_url="/docs",
        redoc_url="/redoc"
    )

    # Add dashboard API endpoints
    fastapi_app = create_dashboard_api(fastapi_app)

    logger.info("Dashboard configured successfully")

    # Mount Gradio app
    return mount_gradio_app(fastapi_app, dashboard, path="/")

