# Define a sample template function for testing
@oneenv
def sample_template():
    """
    Returns a dictionary of environment variable settings for testing.
    """
    return {
        "TEST_VAR": {
            "description": "Test variable description.",
            "default": "value",
            "required": True,
            "choices": ["value", "other"]
        }
    }


